//i am going to pull the response from call login service & store in custom property
import com.eviware.soapui.support.XmlHolder

//To define any variable def is used
//def response = messageExchange.response.responseContent

def response = context.testCase.getTestStepByName("Call CustomerCreation Service").getPropertyValue('response')

//I want to store all the reponse elements in a variable
def holder = new XmlHolder(response)

def id = holder.getNodeValue("//*:id")
def addressid = holder.getNodeValue("//*:result//addresses//item//id")
//log.info sessionid

//i want to store custom property of testcase/testsuote/project
//this is being stored from testcase level
context.testCase.setPropertyValue("id",id)
context.testCase.setPropertyValue("addressid",addressid)
//In a similar way to store it in Project level
context.testCase.testSuite.project.setPropertyValue("id",id)
context.testCase.testSuite.project.setPropertyValue("addressid",addressid)
//To store it in TestSuitelevel
context.testCase.testSuite.setPropertyValue("id",id)
context.testCase.testSuite.setPropertyValue("addressid",addressid)

//I want to apply assertion and the actual value is stored in testcase custom Property
//context.expand will also help you in pulling the element from test step
//def Actualvalue = context.expand('${Call Login Service#Response#//*:sessionId}')
//log.info Actualvalue

//If you are setching it from custom Property it starts with #
//If you are fetching it from Service , it starts with service name 
def Actualvalue = context.expand('${#TestCase#id}')
log.info Actualvalue
def Actualvalue1 = context.expand('${#TestCase#addressid}')
log.info Actualvalue1

def Expectedvalue = id
def Expectedvalue1 = addressid
assert Actualvalue == Expectedvalue

assert Actualvalue1 == Expectedvalue1

