import jxl.*
// To store the path of the xcel
filepath = "C:/Users/KavyaBalasubramanian/Documents/Full Stack Tester/Files/DataDriven/CustomerDetails.xls"
File Exl = new File(filepath)
//When i go to work with excel, we have workbook & sheet
Workbook wb = Workbook.getWorkbook(Exl)
Sheet sh = wb.getSheet("Sheet1")
//i want to fetch the rows & columns from the sheet
def row = sh.getRows().toInteger()
def column = sh.getColumns().toInteger()

log.info "No.of.rows are $row"
log.info "No.of.Columns are $column"

//i am trying to get hte values from the excel sheet
for(def i =1;i<row;i++)
{
	for(def j=0;j<column;j++)
	{
		if(j==0)
		{
			emailid = sh.getCell(j,i).getContents()
		}
		else 
		{
			fname = sh.getCell(j,i).getContents()
			lname = sh.getCell(j,i).getContents()
			regionCode = sh.getCell(j,i).getContents()
			region = sh.getCell(j,i).getContents()
			countryId = sh.getCell(j,i).getContents()
			streetitem = sh.getCell(j,i).getContents()
			phone = sh.getCell(j,i).getContents()
			postalcode = sh.getCell(j,i).getContents()
			city = sh.getCell(j,i).getContents()
			dob = sh.getCell(j,i).getContents()

		}
	}
	//log.info "username is $username and the password is $password"
def tc=testRunner.testCase.testSuite.project.testSuites["End to End Testing"].testCases["Property Expansion for CustomerRepo and CustomerAddressRepo"]
tc.testSteps["Input"].setPropertyValue("emailid",emailid)
