tc.testSteps["Input"].setPropertyValue("region",region)
tc.testSteps["Input"].setPropertyValue("countryid",countryid)
tc.testSteps["Input"].setPropertyValue("streetitem",streetitem)
tc.testSteps["Input"].setPropertyValue("phone",phone)
tc.testSteps["Input"].setPropertyValue("postcode",postcode)
tc.testSteps["Input"].setPropertyValue("city",city)
tc.testSteps["Input"].setPropertyValue("dob",dob)




//whenever there is a change in Property file , just run it no need to update/save anything like Service
